import React from "react";
import * as Icons from "../../../Icons/index";
import { BottomSheetContent, BottomSheetOverlay, BottomSheetWrapper, Icon } from "./BottomSheetNew.styles";
import { BottomSheetNewTypes } from "./BottomSheetNewTypes";

const BottomSheetNew = (props: BottomSheetNewTypes) => {
	const { show, closeBottomSheet, children, backdrop, fullPage, borderRadius } = props;
	return show ? (
		<BottomSheetWrapper>
			{!fullPage && <BottomSheetOverlay onClick={backdrop ? closeBottomSheet : () => null} backdrop={backdrop} />}
			<BottomSheetContent borderRadius={borderRadius} fullPage={fullPage} show={show}>
				<Icon onClick={closeBottomSheet}>
					<Icons.Cross />
				</Icon>
				{children}
			</BottomSheetContent>
		</BottomSheetWrapper>
	) : null;
};

export default BottomSheetNew;