import React from "react";
import Slider, { ResponsiveObject, Settings } from "react-slick";
import CarouselSlide from "./CarouselSlide";

export interface CarouselProps {
  carouselConfig?: Settings
  initialSlide?: number
  responsive?: Array<ResponsiveObject>
  slidesToShow?: number
  wrapperClassName?: string
  sliderClassName?: string
  changeCarouselSlide?: (index: number, imageClick: string) => void
  children: React.ReactNode[]
  isRTL?: boolean
  mobileView?: boolean
  hideArrow?: boolean
  showScrollbar?: boolean
  numberOfElementsCoursel?: number
  cardRatio?: number,
}

export interface SlideProps {
  slideClassName?: string
  id: number
  currentSlide: number
  onClickChangeSlide?: (index: number, imageClick: string) => void
  children: React.ReactNode
}

export interface CarouselType extends React.ForwardRefExoticComponent<CarouselProps & React.RefAttributes<Slider>> {
  Slide: typeof CarouselSlide;
}
