import React, { useEffect } from "react";
import { animated, useSpring } from "react-spring";
import { Checkbox as CheckboxComponent } from "./Checkbox.styles";
import { CheckboxPropsType } from "./Checkbox.types";

const AnimatedCheckbox = animated(CheckboxComponent);


function Checkbox({ checked }: CheckboxPropsType) {
	const [styles, api] = useSpring(() => ({
		backgroundColor: "transparent",
	}));
  
	useEffect(() => {
		if (checked) {
			api({
				backgroundColor: "var(--dark-blue-100)",
			});
		} else {
			api({
				backgroundColor: "transparent",
			});
		}
	}, [checked]);
	return (
		<AnimatedCheckbox style={styles} isActive={checked} />
	);
}

export { Checkbox };