import React from "react";
import { useSpring } from "react-spring";
import { Cross } from "../../../Icons";

import { ScreenContainer,MainSection,Header, Description, HeaderContent, PopUp, CrossSection, PopUpModal, PopUpModalContent } from "./DesktopPopUp.styles";
import { ModalType } from "./DesktopPopUp.types";

const PopupModalDesktop = ({ children, header="", onClickBackDrop, description="", onClose, isCrossIcon }: ModalType) => {
	const springProps = useSpring({
		from: { transform: "translate(50%,-100%)" },
		to: { transform: "translate(0,0)" }
	});
 
	return (<PopUpModal>
		<ScreenContainer onClick={onClickBackDrop}/>
		<PopUp >
			<PopUpModalContent>
				<HeaderContent>
					<Header>{header}</Header>
					{isCrossIcon && <CrossSection onClick={onClose}><Cross/></CrossSection>}
				</HeaderContent>
			
				<Description>{description}</Description>
				<MainSection style={springProps}>
					{children}
				</MainSection>
			</PopUpModalContent>
		</PopUp>
	</PopUpModal>
	);
};

export default PopupModalDesktop;
