import React from "react";
import { InputType } from "./Dropdown.types";
import { ComponentSizeENUM } from "../../../Types/general";
import { InputContainer, InputField, InputLabel } from "./Dropdown.styles";
// import { DownArrow } from "../../../Icons";
const Dropdown = (
	{
		placeholder,
		label,
		// type = inputs.type.text,
		componentSize = ComponentSizeENUM.small,
		id,
		disabled = false,
		error = false,
		width = "50",
		options = [],
		optionsText = "",
		optionValue = "",
		// readonly = false,
		// onValueChange = () => null,
		// onKeyUpChange = () => null,
		// includeButton,
		// buttonText,
		// buttonPosition=positionENUM.right,
		// buttonOnClick,
		font
	}: InputType) => {

	const [inputValue ] = React.useState("");

	// function updateValue(e: React.ChangeEvent<HTMLInputElement>) {
	// 	onValueChange(e);
	// 	action("value change")(e,{ value:e.target.value });
	// }

	// const onKeyUpChangeHandler = (e: React.ChangeEvent<HTMLInputElement>) => {
	// 	onKeyUpChange(e);
	// };

	// function onChange(e: React.ChangeEvent<HTMLInputElement>){
	// 	setInputValue(e.target.value);
	// }
	return <InputContainer id={id} width={width} styleSize={componentSize} styleFont={font} value={inputValue}>
		<InputField list={"datalist"+id} error={error} placeholder={placeholder} disabled={disabled} value={inputValue} />
		<datalist id={"datalist"+id}>
			{options.map((item: any ,key: number)=>{
				return (<option value={item[optionValue] || key} key={key}>
					{item[optionsText] || item}
				</option>);
			})}
		</datalist>
		{/* { {includeButton && <InputButton onClick={buttonOnClick} buttonPosition={buttonPosition} styleFont={font}><DownArrow/></InputButton> } } */}
		<InputLabel>
			{label && !placeholder && label}
		</InputLabel>
	</InputContainer>;
};

export default Dropdown;
