import { DropdownLabel, DropdownWrapper, Option, SelectBox, SortIconContainer } from "./Dropdown.styles";
import { DropdownTypes } from "./Dropdown.types";
import { ComponentSizeENUM, TypographyENUM } from "../../../Types/general";
import React from "react";
import * as Icons from "../../../Icons/index";

const Dropdown = ({
	options,
	componentSize = ComponentSizeENUM.small,
	font = TypographyENUM.serif,
	dataLocale,
	handleChange,
	value,
}: DropdownTypes) => {
	return (
		<DropdownWrapper styleSize={componentSize} styleFont={font}>
			<SortIconContainer><Icons.SortIcon/></SortIconContainer>
			<DropdownLabel>{dataLocale.SORT_BY} </DropdownLabel>
			<SelectBox onChange={(event) => handleChange(event)} value={value}>
				{options.map((item, index) => (
					<Option key={index} value={item.value}>
						{item.key}
					</Option>
				))}
			</SelectBox>
		</DropdownWrapper>
	);
};

export default Dropdown;
