import { DropdownWrapper, Option, SelectBox, DropdownLabel } from "./styles";
import { DropdownTypes } from "./FloatingLabelDropdown.types";
import { ComponentSizeENUM, TypographyENUM } from "../../../Types/general";
import React from "react";
import * as Icons from "../../../Icons/index";

const FloatingLabelDropdown = ({
	options,
	componentSize = ComponentSizeENUM.small,
	font = TypographyENUM.serif,
	value,
	label,
	getInputValue = ()=> null,
	disabled
}: DropdownTypes) => {
	return (
		<DropdownWrapper styleSize={componentSize} styleFont={font}>
			<DropdownLabel> {label} </DropdownLabel>
			<SelectBox disabled={disabled} onChange={(event) => {getInputValue(event.target.value); }} value={value?.toLowerCase()}>
				{options.map((item, index) => (
					<Option key={index} value={item.country_code?.toLowerCase()}>
						{item.country_name}
					</Option>
				))}
			</SelectBox>
			{!disabled && (<span style={{ padding: "16px" }}>
				<Icons.DownArrow />
			</span>)}
		</DropdownWrapper>
	);
};

export default FloatingLabelDropdown;
