type JSON = { [key: string]: JSON | string | number | boolean | null };

export interface GoogleLocationInputProps {
  callback: (value: string) => void;
  label: string;
  configData: JSON;
  initialValue?: string;
  // eslint-disable-next-line @typescript-eslint/no-explicit-any
  disabled?: boolean;
  // eslint-disable-next-line @typescript-eslint/no-explicit-any
  handleError?: (e: React.FocusEvent<HTMLInputElement>) => void;
  storeLocator?: boolean;
}

export type Libraries = "drawing" | "geometry" | "places" | "visualization";
