import React from "react";
import { GridItemProps } from "./Grid.types";
import { GridItemWrapper } from "./styles";

export default function GridItem(props: GridItemProps) {
	const { children, itemCss, itemClassName, itemId } = props;
  
	return (
		<GridItemWrapper className={itemClassName} id={itemId} style={itemCss} {...props}>
			{children}
		</GridItemWrapper>
	);
}
