import React from "react";
import { GridProps } from "./Grid.types";
import GridItem from "./GridItem";
import { GridWrapper } from "./styles";

function Grid(props: GridProps) {
	const { containerCss, containerClassName, containerId, itemCss, itemClassName, itemId, children } = props;

	return (
		<GridWrapper style={containerCss} className={containerClassName} id={containerId} {...props}>
			{
				React.Children.map(children, (child) => {
					if (React.isValidElement(child))
						return React.cloneElement(child, { itemCss, itemClassName, itemId } as any);
				})
			}
		</GridWrapper>
	);
}

Grid.Item = GridItem;

export default Grid;
