import React from "react";
import { DropdownHeaderProps } from "./HoverableDropdown.types";
import { DropdownHeaderWrapper } from "./styles";

export default function DropdownHeader(props: DropdownHeaderProps & React.HTMLAttributes<HTMLDivElement>) {
	const { children } = props;

	return (
		<DropdownHeaderWrapper {...props}>{children}</DropdownHeaderWrapper>
	);
}
