import React from "react";
import { DropdownMenuProps } from "./HoverableDropdown.types";
import { DropdownMenuWrapper } from "./styles";

export default function DropdownMenu(props: DropdownMenuProps & React.HTMLAttributes<HTMLDivElement>) {
	const { children } = props;
  
	return (
		<DropdownMenuWrapper {...props}>
			<div className="inner-wrapper">
				{children}
			</div>
		</DropdownMenuWrapper>
	);
}
