import React from "react";
import { DropdownOptionProps } from "./HoverableDropdown.types";
import { DropdownOptionWrapper } from "./styles";

export default function DropdownOption(props: DropdownOptionProps & React.HTMLAttributes<HTMLDivElement>) {
	const { children } = props;

	return (
		<DropdownOptionWrapper {...props}>{children}</DropdownOptionWrapper>
	);
}
