import React from "react";
import DropdownHeader from "./DropdownHeader";
import DropdownOption from "./DropdownOption";
import { DropdownWrapper } from "./styles";
import { HoverableDropdownProps } from "./HoverableDropdown.types";
import DropdownMenu from "./DropdownMenu";

function HoverableDropdown(props: HoverableDropdownProps & React.HTMLAttributes<HTMLDivElement>) {
	const { children } = props;

	return (
		<DropdownWrapper {...props}>{ children }</DropdownWrapper>
	);
}

HoverableDropdown.Header = DropdownHeader;
HoverableDropdown.Menu = DropdownMenu;
HoverableDropdown.Option = DropdownOption;

export default HoverableDropdown;