export interface HoverableMenuProps {
  header: Header;
  items?: Item[];
  viewAllVisible?: boolean;
  isRTL: boolean;
}

interface Header {
  link: string;
  imgUrl: string;
  title: string;
}

interface Item {
  link: string;
  title: string;
  category?: string;
  price: Price;
}

interface Price {
  marketPrice: string;
  lenskartPrice: string;
  currencyName: string;
  currencySymbol: string;
}
