import React from "react";
import { CommonLoaderProps } from "./Loader.types";
import { CommonLoaderOverlay, CommonLoaderWrapper } from "./styles";

const CommonLoader = ({ wrapperClassName, overlayClassName, show }: CommonLoaderProps) => {
	return show ? (
		<CommonLoaderWrapper className={wrapperClassName}>
			<CommonLoaderOverlay className={overlayClassName} />
			<img
				alt="Loading"
				src="https://static.lenskart.com/media/desktop/img/loader-lk.gif"
				title="Loading"
			/>
		</CommonLoaderWrapper>
	) : null;
};

export default CommonLoader;
