import React from "react";

export interface ModalProps {
  show: boolean;
  dialogClassName?: string;
  dialogCss?: string;
  children: React.ReactNode;
  backdropClassName?: string;
  keyboard?: boolean;
  bsSize?: string;
  position?: Position
  onHide: () => void;
  onBackdropClick?: () => void;
}

export interface ModalWrapperTypes {
  position?: Position
}

export interface ModalBodyProps {
  className?: string;
  children?: React.ReactNode;
  height?: number
}

export interface ModalHeaderProps {
  children?: React.ReactNode;
  closeButton?: boolean;
  onHide: () => void;
  voucherModal?: boolean;
  isRTL?: boolean;
}

export interface ModalTitleProps extends React.HTMLProps<HTMLHeadingElement> {
  children: React.ReactNode;
}

export enum Position {
  top = "top",
  left = "left",
  right = "right",
  bottom = "bottom"
}