import React from "react";
import { OverLay } from "./MoneyModalStyles";
import { StarFilled } from "../../../Icons";
import { MoneyModalType } from "./MoneyModalType";

const MoneyModal = ({ id, isVisible, totalDiscount, closeModal }: MoneyModalType) => {
	return (
		<>
			{isVisible && totalDiscount && (
				<>
					<OverLay id={id}>
						<div className="modalWrapper">
							<div className="offerImage">
								<StarFilled/>
							</div>
							<span className="discountMsg">
								{"You saved  Rs " + Math.ceil(totalDiscount) + " on your final bill"}
							</span>
							<span className="closeButton" onClick={closeModal}>
								CLOSE
							</span>
						</div>
					</OverLay>
				</>
			)}
		</>
	);
};

export default MoneyModal;
