import React from "react";
import { PaginationType } from "./Pagination.types";
import { StyledReactPaginate } from "./styles";

const Pagination = ({ totalOrderCount = 200, itemsPerPage = 5, clickOnPage, setActivePage, activePage = 0 }: PaginationType) => {
	const pageCount = Math.ceil(totalOrderCount / itemsPerPage);
	const handlePageClick = (e:{ selected: number }) => {
		setActivePage(e.selected);
		clickOnPage(e.selected);
	};
	return <StyledReactPaginate
		nextLabel=">"
		onPageChange={handlePageClick}
		pageRangeDisplayed={1}
		marginPagesDisplayed={2}
		pageCount={pageCount}
		forcePage={activePage}
		previousLabel="<"
		pageClassName="page-item"
		pageLinkClassName="page-link"
		previousClassName="page-item"
		previousLinkClassName="page-link"
		nextClassName="page-item"
		nextLinkClassName="page-link"
		breakLabel="..."
		breakClassName="page-item"
		breakLinkClassName="page-link"
		containerClassName="pagination"
		activeClassName="active"
	/>;
};

export default Pagination;
