import React, { useEffect } from "react";
import { PopUpContent, Root } from "./PopUpText.styles";
import { PopUpTextType } from "./PopUpText.types";

export default function PopUpText({ setMsgDrop, text = "", isRTL=false, top = false }: PopUpTextType) {
	useEffect(() => {
		const timer = setTimeout(() => {
			// setMsgDrop(false);
			if(typeof setMsgDrop === "function") setMsgDrop(true);
		}, 1000);

		return(() => {
			clearTimeout(timer);
		});
	}, [text]);

	if (!text) return null;
	return (
		<Root isRTL={isRTL} top={top}>
			<PopUpContent>{text}</PopUpContent>
		</Root>
	);
}
