import React from "react";
import {
	ComponentSizeENUM,
	ThemeENUM,
	TypographyENUM,
} from "../../../Types/general";
import { PriceTypes } from "./Price.types";
import { Container, CurrencySpan, GstText, PricingSpan, SpecialPriceSpan, TaxText } from "./Price.styles";

const Pricing = ({
	actualPrice = 2999,
	finalPrice = 999,
	hasSpecialPrice = false,
	componentSize = ComponentSizeENUM.medium,
	font = TypographyENUM.serif,
	styleTheme = ThemeENUM.primary,
	currencyCode,
	finalPriceColor,
	actualPriceColor,
	isDiscountPriceBigger=false,
	strikeThroughColor,
	isRTL = false,
	isGridView= false,
	isMobileView = false,
	desktopPriceFontBold,
	dataLocale,
	showTax = false,
	taxInclusivePrice,
	taxFontSize
}: PriceTypes) => {
	return (
		<Container
			styleSize={componentSize}
			styleFont={font}
			data-testid="pricing-test"
			styleTheme={styleTheme}
			isRTL={isRTL}
			padding="0px"
		>
			<SpecialPriceSpan desktopPriceFontBold={desktopPriceFontBold} isGridView={isGridView} finalPriceColor={finalPriceColor} isMobileView={isMobileView}>
				<CurrencySpan>{currencyCode}</CurrencySpan>
				<span>{!hasSpecialPrice ? actualPrice : finalPrice}</span>
				{/* {isRTL && <CurrencySpan>{currencyCode}</CurrencySpan>} */}
			</SpecialPriceSpan>
			{hasSpecialPrice &&
			<PricingSpan isGridView={isGridView} strikeThrough={true} strikeThroughColor={strikeThroughColor} desktopPriceFontBold={desktopPriceFontBold} isMobileView={isMobileView} isDiscountPriceBigger={isDiscountPriceBigger} actualPriceColor={actualPriceColor}>
				<CurrencySpan>{currencyCode}</CurrencySpan>
				<span>{actualPrice}</span>
				{/* {isRTL && <CurrencySpan>{currencyCode}</CurrencySpan>}  */}
			</PricingSpan>
			}
			{showTax && <TaxText taxFontSize={taxFontSize}>(+{dataLocale?.TAX})</TaxText>}
			{taxInclusivePrice && <GstText>({dataLocale?.CURRENCY_SYMBOL}{taxInclusivePrice}{" "}{dataLocale?.WITH_GST})</GstText>}
			{/* <PricingSpan strikeThrough={false} strikeThroughColor={strikeThroughColor} isDiscountPriceBigger={isDiscountPriceBigger} actualPriceColor={actualPriceColor}>
				{!isRTL && <span>(+ tax)</span>}
			</PricingSpan> */}
		</Container>   
	);
};

export default Pricing;
