import React from "react";
import { PrimaryButtonType } from "./PrimaryButton.types";
import { Img, PrimaryButtonWrapper, PrimaryText, SecondaryText } from "./styles";

const PrimaryButton = ({ disabled = false, id, width, height, primaryText, secondaryText, onBtnClick, font, componentSize, backgroundColor, borderColor, color = "white", mobileView, icon = "", padding, positionRight, borderRadius = "", weight }:PrimaryButtonType) =>{
	return (
		<PrimaryButtonWrapper positionRight={positionRight} mobileView={mobileView} color={color} disabled={disabled} backgroundColor={backgroundColor} id={id} width={width} height={height} onClick={onBtnClick} styleFont={font} styleBorder={borderColor} icon={icon} padding={padding} borderRadius={borderRadius}>
			{icon && (<Img src={icon} />)}
			<PrimaryText componentSize={componentSize} weight={weight}>{primaryText}
				{secondaryText && <SecondaryText>{secondaryText}</SecondaryText>}
			</PrimaryText>
		</PrimaryButtonWrapper>
	);
};

export default PrimaryButton;
