import styled from "styled-components";
import { ComponentSizeENUM } from "../../../Types/general";

function getComponentSize(size:ComponentSizeENUM){
	if (size === ComponentSizeENUM.small) {
		return "var(--fs-12)";
	} else if (size === ComponentSizeENUM.medium) {
		return "var(--fs-14)";
	} else {
		return "24px";
	}
}

export const RatingContainer = styled.div<{componentSize:ComponentSizeENUM, isReadOnly:boolean, isRTL: boolean}>`
display:flex;
align-items:center;
flex-wrap:wrap;
/* gap:0.25em; */
cursor:${props => props.isReadOnly ? "default" : "pointer"};
font-size:${props => getComponentSize(props.componentSize)};
${props => props.isRTL ? "flex-direction: initial" : ""};
svg {
padding: 0 0.25em 0 0;
box-sizing: content-box;
}
`;
