import React from "react";
import { Root } from "./ResultPopUp.styles";
import { ResultPopUPType } from "./ResultPopUp.types";

export default function ResultPopUp({
	color = "#a94442",
	background = "#f2dede",
	border = "#ebccd1",
	message,
	width,
}: ResultPopUPType) {
	return (
		<Root color={color} background={background} border={border} width={width}>
			{message}
		</Root>
	);
}
