import React from "react";
import * as Icons from "../../../Icons/index";
import { Content, Fade, Header, IconWrapper, SideModalWrapper, Title } from "./SideModal.styles";
import { SideModalTypes } from "./SideModalTypes";

const SideModal = (props: SideModalTypes) => {
	const { children, title, isRTL = false, centerHeading = false, isVisible, backButton, onClose, backgroundColor } = props;
	return (
		<SideModalWrapper isVisible={isVisible}>
			<Fade onClick={onClose} />
			<Content isRTL={isRTL} backgroundColor={backgroundColor}>
				<Header isRTL={isRTL}>
					<IconWrapper onClick={backButton}>
						{isRTL ? <Icons.RightArrow onClick={backButton} /> : <Icons.BackArrow onClick={backButton} />}
					</IconWrapper>
					<Title centerHeading={centerHeading}>
						<h2>
							{title}
						</h2>
					</Title>
					<IconWrapper onClick={onClose}>
						<Icons.Cross onClick={onClose}/>
					</IconWrapper>
				</Header>
				{children}
			</Content>
		</SideModalWrapper>
	);
};

export default SideModal;