import React from "react";
import { SpinnerTypes } from "./Spinner.types";
import { SpinnerLoader, SpinnerWrapper } from "./styles";

const Spinner = (props: SpinnerTypes) => {
	const { show = true, fullPage = false, padding = "",isAlgoliaSearch = false,backgroundColor } = props;
	return show ? (
		<SpinnerWrapper fullPage={fullPage} padding={padding} backgroundColor={backgroundColor}>
			<SpinnerLoader isAlgoliaSearch={isAlgoliaSearch}/>
		</SpinnerWrapper>
	) : null;
};

export default Spinner;