import React from "react";
import { List } from "../tabs.styles";

const Tab = (props: any) => {
	const { eventKey, title, children, className, onSelect } = props;
	return (
		<List
			className={title.className ? title.className : className}
			role="presentation"
			onClick={() => onSelect(eventKey)}
		>
			<a
				aria-controls={`controlled-tab-example-pane-${eventKey}`}
				id={`controlled-tab-example-tab-${eventKey}`}
				role="tab"
			>
				{title !== undefined ? title : children}
			</a>
		</List>
	);
};

export default Tab;
