import React from "react";
import { CmnToggleLabelWrapper, ToggleWrapper, TextContainer } from "./styles";
import { ToggleButtonTypes } from "./ToggleButton.types";

const ToggleButton = ({ text, checked, clickOnToggle, outerWidth , outerHeight, innerWidth, innerHeight, innerColor, outerColor }: ToggleButtonTypes) => {
	const clickToggle = () => {
		clickOnToggle();
	};
	return <ToggleWrapper onClick={clickToggle}>
		<CmnToggleLabelWrapper checked={checked} outerWidth={outerWidth} outerHeight={outerHeight} innerWidth={innerWidth} innerHeight={innerHeight} innerColor={innerColor} outerColor={outerColor} />
		{text && <TextContainer>{text}</TextContainer>}
	</ToggleWrapper>;
};

export default ToggleButton;