import React from "react";
import { TryOnTypes } from "./TryOn.types";
import { TryOnWrapper, TryOnImage } from "./styles";

const TryOn = ({ id, width, tryOnImageURL, imagePosition, height, onBtnClick, isRTL=false, borderColor = "", backgroundColor="", text }: TryOnTypes) => {
	return (
		<TryOnWrapper id={id} data-testid={id} width={width} height={height} onClick={onBtnClick} borderColor={borderColor} backgroundColor={backgroundColor}
		>
			<span>{text}</span>
			<div>
				{!isRTL && <TryOnImage
					imagePosition={imagePosition}
					alt="Try On"
					src={tryOnImageURL}
					isRTL={isRTL}
				/>}
				{isRTL && <TryOnImage
					imagePosition={imagePosition}
					alt="Try On"
					src={tryOnImageURL}
					isRTL={isRTL}
				/>}
			</div>
		</TryOnWrapper>
	);
};

export default TryOn;
