import React from "react";
import { Backdrop, BottomSheetContainer, BottomSheetDiv } from "./BottomSheet.Styles";
import { BottomSheetTypes } from "./BottomSheet.types";

export default function BottomSheet({ backdrop, onClickBackdrop, expanded, addTopRadius, pageContext, sixOverSix, borderRadius, addressEditOrderDetail, bottomSheetCss, backdropCss, children, darkBack }: BottomSheetTypes) {
	return (
		<BottomSheetContainer
			aria-label="bottom-sheet-container"
			expanded={expanded}
			heightExpand={addressEditOrderDetail}
		>
			<React.Fragment>
				{backdrop && expanded && (
					<Backdrop
						aria-label="backdrop"
						expanded={expanded}
						passedCss={backdropCss}
						onClick={() => typeof onClickBackdrop === "function" && onClickBackdrop()}
						darkBack={darkBack}
					></Backdrop>
				)}
				<BottomSheetDiv
					aria-label="bottom-sheet"
					expanded={expanded}
					heightExpand={addressEditOrderDetail}
					sixOverSix={sixOverSix}
					bottomSheetCss={bottomSheetCss}
					topBorder={addTopRadius || pageContext === "category" || pageContext === "search"}
					borderRadius={borderRadius}
				>
					{children}
				</BottomSheetDiv>
			</React.Fragment>
		</BottomSheetContainer>
	);
}
