import React from "react";
import { AddDeleteItemContainer, ItemQuantity } from "./AddDeleteItem.styles";
import { AddDeleteItemType } from "./AddDeleteItem.types";

const AddDeleteItem = ({ quantity = 0, onIncrement, onDecrement }: AddDeleteItemType) => {
	return (
		<AddDeleteItemContainer>
			<div onClick={onDecrement}>
				<span>
					<img
						alt="minus-icon"
						src="https://static1.lenskart.com/media/desktop/img/DesignStudioIcons/MinusIcon.svg"
					/>
				</span>
			</div>
			{quantity && <ItemQuantity>{quantity}</ItemQuantity>}
			<div onClick={onIncrement}>
				<span>
					<img
						alt="plus-icon"
						src="https://static1.lenskart.com/media/desktop/img/DesignStudioIcons/Plus.svg"
					/>
				</span>
			</div>
		</AddDeleteItemContainer>
	);
};

export default AddDeleteItem;
