import React from "react";
import { TypographyENUM } from "../../../Types/general";
import Accordion from "./Accordion.component";
import { FAQsTypes } from "./FQAs.types";
import { FQADiv, FQAsWrapper, AccordionWrapper, AccordionChildrenWrapper } from "./styles";

const FAQs = ({ dataLocale, AccordionTitleText } : FAQsTypes) => {
	return (
		<FQAsWrapper>
			<FQADiv>{dataLocale.FAQS}</FQADiv>
			{
				AccordionTitleText?.map((item: any, index: number) => {
					return (<AccordionWrapper key={`fqas_${index}`}>
						<Accordion font={TypographyENUM.lkSansMedium} icon={""} btnText={""} showBorderTop={false} title={item.question} isMaxHeight={false} isRTL={false} canCollapse={true} isComponent={false}>
							<AccordionChildrenWrapper dangerouslySetInnerHTML={{ __html : item.answer }}></AccordionChildrenWrapper>
						</Accordion>
					</AccordionWrapper>);
				})
			}
		</FQAsWrapper>
	);
};

export default FAQs;