import React from "react";
import Slider from "react-slick";
import Carousel from "../../Common/Carousel";
import { HtoSixSignsCourselType } from "./HtoSixSignsCoursel.types";
import { HtoSixSignsCourselWrapper, SixSignsWrapper, SixNumber, SixText, CousealWrapper, CourselContainer } from "./styles";
import CoursealCard from "../CoursealCard";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";

const HtoSixSignsCoursel = ({ dataLocale, isRTL, courselConfig, courselArrayObject }: HtoSixSignsCourselType) => {
	const carouselRef = React.useRef<Slider>(null);

	return (
		<HtoSixSignsCourselWrapper>
			<SixSignsWrapper>
				<SixNumber>{dataLocale.SIX}</SixNumber>
				<SixText>{dataLocale.SIGNS_YOU} <br/>{dataLocale.NEED_AN_EYETEST}</SixText>
			</SixSignsWrapper>
			<CousealWrapper>
				<Carousel initialSlide={1} showScrollbar={true} numberOfElementsCoursel={courselArrayObject?.length} wrapperClassName="image-slider" isRTL={isRTL} slidesToShow={courselConfig?.slidesToShow} carouselConfig={{ infinite: true, autoplay: courselConfig?.autoPlay, ...courselConfig }} mobileView={false} ref={carouselRef} hideArrow={true}>
					{
						courselArrayObject?.map((item: { heading1: string, heading2: string, icon: string }, index: number) => {
							return (
								<Carousel.Slide id={index} currentSlide={1} key={index}>
									<CourselContainer>
										<CoursealCard heading1={item.heading1} heading2={item.heading2} icon={item.icon} />
									</CourselContainer>
								</Carousel.Slide>
							);
						})
					}
				</Carousel>
			</CousealWrapper>
		</HtoSixSignsCourselWrapper>
	);
};

export default HtoSixSignsCoursel;