import React from "react";
import { ProductNamePriceType } from "./ProductName.types";
import { ProductNamePriceWrapper, ProductNameWrapper, Heading, SubHeading, ProductPriceWrapper, ProductActualPrice, ProductSellingPrice, ProductDescription } from "./styles";


const ProductNamePrice = ({ heading, subHeading, actualPrice, sellingPrice, dataLocale } : ProductNamePriceType) => {
	return (<ProductNamePriceWrapper>
		<ProductNameWrapper>
			<Heading>{heading}</Heading>
			<ProductPriceWrapper>
				<ProductActualPrice>{dataLocale.CURRENCY_SYMBOL} {actualPrice}</ProductActualPrice>
				<ProductSellingPrice>{dataLocale.CURRENCY_SYMBOL} {sellingPrice}</ProductSellingPrice>
			</ProductPriceWrapper>
		</ProductNameWrapper>
		<ProductDescription>
			<SubHeading>{subHeading}</SubHeading>
		</ProductDescription>
	</ProductNamePriceWrapper>
	);
};

export default ProductNamePrice;