import React from "react";
import { TypographyENUM } from "../../../Types/general";
import Accordion from "./Accordion.component";
import { TermsAndConditionContainer, AccordionChildrenWrapper, List } from "./styles";
import { TermsAndConditionTypes } from "./TermsAndConditions.types";

const TermsAndCondition = ({ dataLocale, redisCommonData } : TermsAndConditionTypes) => {
	const termsAndConditionsArray = redisCommonData && redisCommonData.TERMS_AND_CONDITIONS && JSON.parse(redisCommonData.TERMS_AND_CONDITIONS);
	return <TermsAndConditionContainer>
		<Accordion font={TypographyENUM.lkSansMedium} icon={""} btnText={""} showBorderTop={false} title={dataLocale.TERMS_AND_CONDITION} isMaxHeight={false} isRTL={false} canCollapse={true} isComponent={false}>
			<AccordionChildrenWrapper>
				{
					termsAndConditionsArray && termsAndConditionsArray.map((item:string, index:number) => {
						return <List key={`terms_conditions_${index}`}>
							{item}
						</List>;
					})
				}
			</AccordionChildrenWrapper>
		</Accordion>
	</TermsAndConditionContainer>;
};

export default TermsAndCondition;
