import React from "react";
import { WhatToExceptText, WhatToExceptWrapper, IconTextWrapper, IconWrapper, TextWrapper, IconTextContainer, Icon } from "./styles";
import { WhatToExceptArrayType, WhatToExceptTypes } from "./WhatToExcept.types";

const WhatToExcept = ({ dataLocale, WhatToExceptArray }: WhatToExceptTypes) => {
	return <WhatToExceptWrapper>
		<WhatToExceptText>{dataLocale.WHAT_TO_EXCEPT}</WhatToExceptText>
		<IconTextWrapper>
			{
				WhatToExceptArray.map((item: WhatToExceptArrayType, index:number) => {
					return <IconTextContainer key={`image_${index}`}>
						<IconWrapper>
							<Icon src={item.icon} alt="image" />
						</IconWrapper>
						<TextWrapper>{item.text}</TextWrapper>
					</IconTextContainer>;
				})
			}
		</IconTextWrapper>
	</WhatToExceptWrapper>;
};

export default WhatToExcept;
