import React from "react";
import { AccordionTypes } from "./Accordion.types";
import {
	AccordionWrapper,
	TitleBox,
	TitleWrapper,
	Title,
	Content,
	DownArrow,
	UpArrow,
} from "./styles";

const Accordion = ({
	id,
	children,
	title,
	isMaxHeight,
	expand,
	handleExpand,
	showBorderTop,
	canCollapse,
	isComponent,
	font,
	isRTL = false,
	padding = false,
	height
}: AccordionTypes) => {
	return (
		<AccordionWrapper id={id} borderTopStyle={showBorderTop} expand={expand} styleFont={font} isRTL={isRTL} >
			<TitleBox styleComponent={isComponent} onClick={() => handleExpand()} expand={expand} isRTL={isRTL} padding={padding}>
				<TitleWrapper>
					<Title className="accTitle" isRTL={isRTL}>{title}</Title>
				</TitleWrapper>
				{canCollapse && <i>{!expand ? <UpArrow /> : <DownArrow />}</i>}
			</TitleBox>
			<Content isMaxHeight={isMaxHeight} height={height} expand={expand} padding={padding} className="acoContent">{children}</Content>
		</AccordionWrapper>
	);
};

export default Accordion;
