import React from "react";
import { TypographyENUM } from "../../../Types/general";
import { SwatchContainer, SwatchImg, SwatchRow, SwatchText } from "./styles";

interface SwatchType {
	options: {icon: string | React.ReactNode, text: string}[],
	font: TypographyENUM,
	onChange: (text: string) => void,
	value: string
}
const AddressSwatch = ({ options, font, value, onChange }: SwatchType) => {

	// const [selected, setSelected] = useState("");

	return (
		<SwatchRow font={font}>
			{options.map(op => <SwatchContainer key={op.text} onClick={() => {onChange(op.text);}} selected={value === op.text}>
				{op.icon && (typeof op.icon === "string" ? <SwatchImg src={op.icon} alt=""/> : op.icon) || <div />}
				<SwatchText>{op.text}</SwatchText>
			</SwatchContainer>)}
		</SwatchRow>
	);
};

export default AddressSwatch;
