import React from "react";
// import "./MenuItem.scss";
import { MenuItemType } from "./MenuItem.types";
import { MenuItListItem, MenuItRipple } from "./styles";

const MenuItem = ({
	value,
	onChange,
	selectedValue,
	displayVal,
	disabled = false,
	menuItemStyles,
	children,
}: MenuItemType) => {
	return (
		<MenuItListItem
			aria-disabled={disabled}
			aria-selected={selectedValue === value}
			disabled={disabled}
			role="option"
			style={{ ...menuItemStyles }}
			tabIndex={-1}
			value={value}
			onClick={onChange}
		>
			{displayVal || children}
			{!disabled && <MenuItRipple />}
		</MenuItListItem>
	);
};

export default MenuItem;
