import React from "react";

interface HighlightProps {
  hit: {
    _highlightResult?: {
      productId?: {
        value?: string;
      };
    };
  };
  attribute:string
}

export function Highlight({ hit }: HighlightProps) {
	const highlightedText: string[] = [];
	const data = hit?._highlightResult?.productId?.value?.split("/") || [];
	const regex = /\d+/g;
	data.forEach((item: string) => {
		const matches = item.match(regex);

		if (matches) {
			highlightedText.push(...matches);
		}
	});
	return (
		<div className="aa-HighlightPid">
			<span>{highlightedText[0]}</span>
			<mark>{highlightedText[1]}</mark>
		</div>
	);
}
