import React from "react";
import { Icons } from "../../../..";
import { CardImageWrapper, CardItemAction, CardItemContent, CardItemSeperator, CardItemTileWrapper, CardItemTitle } from "../common.styles";
import {
	CardTitle,
	CardWrapper,
	NoResultWrapper,
	TextWrapper,
} from "./styles";

interface NoResultPageTypes {
  query:string,
  isError:boolean,
  noResultData:NoResultTypes
}
interface NoResultTypes {
  NO_RESULT:[],
}

interface CategoryTypes{
  data:[],
  name:string
}
interface DataItemTypes{
  imageUrl:string,
  id:string,
  title:string,
  url:string
}


const NoResultPage = ({ query,isError,noResultData }:NoResultPageTypes) => {
	return (
		<NoResultWrapper>
			<TextWrapper>
				{isError?"Oops! Something went wrong!":` Looks like "${query}" didn't turn up any results, explore the below categories to discover more.`}
			</TextWrapper>
			{
				noResultData?.NO_RESULT?.map((category:CategoryTypes,index:number)=> <CardWrapper key={index}>
					<CardTitle>{category.name}</CardTitle>
					{
						category?.data?.map((item:DataItemTypes,index:number)=><>
							<CardItemTileWrapper key={item.id} onClick={()=>window.location.href= item.url}>
								<CardItemContent>
									<CardImageWrapper src={item.imageUrl} height={40} width={40}>
              
									</CardImageWrapper>

									<CardItemTitle>{item.title}</CardItemTitle>
								</CardItemContent>
								<CardItemAction>
									<Icons.FillUp height={"12"} width={"12"}/>
								</CardItemAction>
							</CardItemTileWrapper>
							<CardItemSeperator showBorder={index!=category.data.length-1} />
						</>)
					}
				</CardWrapper>)
			}
		</NoResultWrapper>
	);
};

export default NoResultPage;
