import React, { useEffect } from "react";

const SampleDiv = ({ setRecaptchaInDom, setisCaptchaMounted }: { setRecaptchaInDom: (arg: boolean) => void, setisCaptchaMounted: (arg: boolean) => void }) => {
	useEffect(() => {
		if(setRecaptchaInDom && typeof setRecaptchaInDom === "function") setRecaptchaInDom(true);
		return () => {
			if(setRecaptchaInDom && typeof setRecaptchaInDom === "function") setRecaptchaInDom(false);
			if(setisCaptchaMounted && typeof setisCaptchaMounted === "function") setisCaptchaMounted(false);
		};
	}, []);
	return (
		<div></div>
	);
};

export default SampleDiv;
