import React from "react";
import SignInForm from "../SignInForm";
import { SignUpForm } from "../SignUpForm/SignUpForm.component";
import { AuthorizationType } from "./Authorization.types";
import { AuthorizationContainer, BannerWrapper, Banner, AuthWrapper, AuthHeader, AuthHeaderTab } from "./styles";

const Authorization = ({
	id,
	dataLocale,
	onSignUp,
	onSignIn,
	font,
	onProceed,
	signInStatus }: AuthorizationType) => {
	const [signInSelected, setsignInSelected] = React.useState(true);

	return <AuthorizationContainer id={id} styleFont={font}>
		<BannerWrapper>
			<Banner src={ signInSelected ? "https://static.lenskart.com/media/wysiwyg/sign-register/sign-in-offer.jpg" : "https://static1.lenskart.com/media/desktop/img/sign-up.png"} alt={dataLocale.BANNER}/>
		</BannerWrapper>
		<AuthWrapper>
			<AuthHeader>
				<AuthHeaderTab selected={!signInSelected} onClick={() => setsignInSelected(false)}>Sign Up</AuthHeaderTab>
				<AuthHeaderTab selected={signInSelected} onClick={() => setsignInSelected(true)}>Sign In</AuthHeaderTab>
			</AuthHeader>
			{signInSelected ?
				<SignInForm
					signInStatus={signInStatus}
					font={font}
					id="sign-up-form"
					dataLocale={dataLocale}
					isRTL={false}
					onSignIn={onSignIn}
					onProceed = {(fieldType, value, password)=> onProceed(fieldType, value, password)}
					isLoginSuccess = {false}
					showOTPView = {false}
					countryCode= "+91"/> :
				<SignUpForm font={font} id="sign-up-form" dataLocale={dataLocale} onSignUp={onSignUp} isRTL={false} />}
		</AuthWrapper>
	</AuthorizationContainer>;
};

export default Authorization;
