import React from "react";
import {
	AddDeleteGroupWrapper,
	DeleteButtonWrapper,
	AddButtonWrapper,
	QuantitySpan,
	FlexWrapper,
} from "./styles";
import { AddDeleteGroupTypes } from "./AddDeleteGroup.types";
import { PlusIcon, Minus } from "../../../../Icons";

/**
 * Default AddDeleteGroup Component
 */
const AddDeleteGroup = ({
	id,
	quantity,
	onAddClick,
	onDeleteClick,
}: AddDeleteGroupTypes) => {
	return (
		<AddDeleteGroupWrapper id={id} onClick={(ev) => {ev.stopPropagation();}}>
			<FlexWrapper>
				<DeleteButtonWrapper onClick={onDeleteClick}>
					<Minus />
				</DeleteButtonWrapper>
				<QuantitySpan>{quantity}</QuantitySpan>
				<AddButtonWrapper onClick={onAddClick}>
					<PlusIcon />
				</AddButtonWrapper>
			</FlexWrapper>
		</AddDeleteGroupWrapper>
	);
};
export default AddDeleteGroup;
