import React from "react";
import {
	AlertWrapper,
} from "./styles";
import { AlertTypes } from "./Alert.types";

/**
 * Default Alert Component
 */
const Alert = ({
	id,
	children,
	color,
	componentSize,
	font,
	boxShadow = false
}: AlertTypes) => {
	return (
		<AlertWrapper id={id} bgColor={color} styleSize={componentSize} styleFont={font} boxShadow={boxShadow}>
			{children}
		</AlertWrapper>
	);
};
export default Alert;
