import React from "react";
import {
	ApplyCouponWrapper,
	RightWrapper,
	FlexWrapper,
	FlexColWrapper,
	HeadText,
	SubText,
	IconWrapperButton,
	Root
} from "./styles";
import { ApplyCouponTypes } from "./ApplyCoupon.types";
import { RightArrow } from "../../../../Icons";
import { ThemeENUM, TypographyENUM } from "../../../../Types/general";
import { kindENUM } from "../../Button/Button.types";
import Button from "../../Button";

/**
 * Default ApplyCoupon Component
 */
const ApplyCoupon = ({
	id,
	width,
	headText,
	subText,
	onClick,
	onRemoveClick,
	font,
	isApplied,
	isRTL = false,
	showAnimation = true,
	isMobileView
}: ApplyCouponTypes) => {
	return (
		<Root showAnimation={showAnimation} isMobileView={isMobileView}>
			<ApplyCouponWrapper id={id} width={width} onClick={onClick} style={{ cursor: "pointer" }}>
				<FlexWrapper isMobileView={isMobileView}>
					<FlexColWrapper>
						<HeadText>{headText}</HeadText>
						{subText && <SubText styleFont={font} isApplied={isApplied} isRTL={isRTL}>{subText}</SubText>}
					</FlexColWrapper>
					<RightWrapper>
						{isApplied ? <Button style={{ padding: "0", textTransform: `${isMobileView ? "capitalize" : "uppercase"}`, textDecoration: "none", fontSize: `${isMobileView ? "12px" : "14px"}` }} hoverShadow={false} width={"65"} isRTL={isRTL} id="coupon-removed-button" font={TypographyENUM.lkSansBold} showChildren={true} text="Remove" theme={ThemeENUM.secondary} kind={kindENUM.tertiary} onClick={(e)=>{
							e.stopPropagation();
							onRemoveClick();}} >Remove</Button> : <IconWrapperButton isRTL={isRTL}><RightArrow/></IconWrapperButton>}
					</RightWrapper>
				</FlexWrapper>
			</ApplyCouponWrapper>
		</Root>
	);
};
export default ApplyCoupon;
