import React from "react";
import Spinner from "../../Common/Spinner";
import { Heading, ImageContainer, SubHeading, Wrapper } from "./styles";
import { DataType } from "../../../Types/general";

const ChatBotBackground = ({
	loader,
	localeData,
}: {
  loader: boolean;
  localeData: DataType;
}) => {
	const {
		CHATBOT_BG_IMG,
		CUSTOMER_SUPPORT,
		SUPPORT_WIDGET_WILL_LOAD_IN_A_MOMENT,
	} = localeData || {};

	return (
		<Wrapper>
			<ImageContainer
				alt={CHATBOT_BG_IMG}
				aria-label="ChatBot Backgroung Img"
				src="https://static.lenskart.com/media/mobile/universal/images/orderStatus/customer-support.png"
			/>
			<Heading>
				{CUSTOMER_SUPPORT} {loader && <Spinner show fullPage />}
			</Heading>
			<SubHeading>{SUPPORT_WIDGET_WILL_LOAD_IN_A_MOMENT}</SubHeading>
		</Wrapper>
	);
};

export default ChatBotBackground;
