import React from "react";
import Email from "./components/Email";
import NormalField from "./components/NormalField";
import Number from "./components/Number";
import Phone from "./components/Phone";
import RadioGroup from "./components/RadioGroup";
import SelectCountry from "./components/SelectCountry";
import SelectState from "./components/SelectState";
// import AddressInputField from "../components/AddressInputField";
// import AddressTextField from "../components/AddressTextField";
import { FieldTypes } from "./types";

const ComponentMapper = (props: any) => {
	switch (props.fieldType) {
	case FieldTypes.NORMAL:
		return <NormalField {...props} />;

	case FieldTypes.RADIO:
		return <RadioGroup {...props} />;

	case FieldTypes.PHONE:
		return <Phone {...props}/>;

	case FieldTypes.EMAIL:
		return <Email {...props}/>;

	case FieldTypes.NUMBER:
		return <Number {...props}/>;

	case FieldTypes.SELECT_STATE:
		return <SelectState {...props}/>;

	case FieldTypes.SELECT_COUNTRY:
		return <SelectCountry {...props}/>;

	default:
		return null;
	}
};

export default ComponentMapper;
