 
  
import React, { Fragment } from "react";
import {
	AddressBox,
	AddressButton,
	AddressColumn,
	AddressData,
	AddressHead,
	AddressRow,
	ButtonGroup,
	ButtonOuter,
} from "../styles";
import { ComponentSizeENUM, ThemeENUM, TypographyENUM } from "../../../../Types/general";
import * as Icons from "../../../../Icons/index";
import PrimaryButton from "../../../Common/PrimaryButton";
  
const NewAddressTab = ({
	dataLocale = {
		EDIT: "Edit",
		PHONE: "Phone"
	},
	userInfo,
	selectedAddressIndex,
	onClick,
	onEdit,
	onDelete,
	font,
}: any) => {
	const {
		id,
		firstName,
		lastName,
		phone,
		phoneCode,
		region,
		addressline1,
		addressline2,
		city,
		state,
		postcode,
	} = userInfo;
  
	return (
		<>
			<AddressBox id={id} styledFont={font}>
				<AddressRow>
					<AddressHead font={TypographyENUM.lkSansBold}>
						{firstName} {lastName}
					</AddressHead>
					<ButtonGroup>
						<AddressButton
							font={TypographyENUM.lkSansRegular}
							onClick={() => onEdit(id)}
						>
							{dataLocale.EDIT}
						</AddressButton>
						<AddressButton onClick={() => onDelete(id)}>
							<Icons.TrashFilled />
						</AddressButton>
					</ButtonGroup>
				</AddressRow>
				<AddressColumn>
					<AddressData font={TypographyENUM.lkSansRegular}>
						{addressline1}
					</AddressData>
					<AddressData font={TypographyENUM.lkSansRegular}>
						{addressline2}
					</AddressData>
					<AddressData font={TypographyENUM.lkSansRegular}>
						{region}
					</AddressData>
					<AddressData font={TypographyENUM.lkSansRegular}>
						{city}, {state}, {postcode}
					</AddressData>
					<AddressData font={TypographyENUM.lkSansRegular}>
						{dataLocale.PHONE} - {phoneCode} {phone}
					</AddressData>
					
				</AddressColumn>
				{selectedAddressIndex.id === userInfo.id ? (
					<ButtonOuter>
						<PrimaryButton
							primaryText={"PROCEED"}
							font={TypographyENUM.serif}
							componentSize={ComponentSizeENUM.medium}
							onBtnClick={onClick}
							id="btn-primary-cl"
							width={"17%"}
							height="35px"
							theme={ThemeENUM.primary}
						/>
					</ButtonOuter>
				) : null}
			</AddressBox>
		</>
	);
};
  
export { NewAddressTab };