import React from "react";
import {
	BrandName,
	HeaderOuter,
	Heading,
	DarkColorPrice,
	ProductId,
	RPDImgOuter,
	TextTransform,
	Div
} from "../styles";

const ReturnProductDetails = (props: any) => {
	const { item, exchangewith = false, returnedProductPrice, lensPackage, coating, price, configTemp } = props;

	const {
		brandName,
		thumbnail,
		productTypeValue,
		modelName,
		contactDisposableType,
		frameColour,
		frameShape,
		frameType,
	} = item || {};

	return (
		<Div>
			<Heading exchangewith={exchangewith}>
				{!exchangewith ? "Product Returned" : "Exchanged With"}
			</Heading>
			<RPDImgOuter>
				<img
					alt={"PRODUCT_IMG"}
					className="margin-t15"
					src={thumbnail}
					width="84%"
				/>
			</RPDImgOuter>
			<HeaderOuter>
				{brandName && <BrandName>{brandName}</BrandName>}
			</HeaderOuter>
			{frameColour && frameShape && (
				<ProductId>
					{frameColour} {frameShape}
					{frameType && (
						<TextTransform> {frameType.replace("_", " ")}</TextTransform>
					)}
				</ProductId>
			)}
			{lensPackage && <ProductId>+ {lensPackage}</ProductId>}
			{coating && (
				<ProductId>
          + {coating}
				</ProductId>
			)}
			{productTypeValue !== "Eyeglasses" &&
        productTypeValue !== "Sunglasses" &&
        modelName && <ProductId>{modelName}</ProductId>}
			{productTypeValue === "Contact Lens" && contactDisposableType && (
				<ProductId>{contactDisposableType}</ProductId>
			)}
			{returnedProductPrice > Math.round(price) ? (
				<DarkColorPrice>
					{configTemp.CURRENCY_SYMBOL}{" "}
					{(returnedProductPrice && Math.round(returnedProductPrice)) || 0}
				</DarkColorPrice>
			) : (
				<DarkColorPrice>
					{configTemp.CURRENCY_SYMBOL} {(price && Math.round(price)) || 0}
				</DarkColorPrice>
			)}
		</Div>
	);
};

export { ReturnProductDetails };
